jQuery(function($){if(window.IntersectionObserver){var observer=new window.IntersectionObserver(function(entries){var entry=entries[0];var isOut=entry.intersectionRatio<1&&entry.boundingClientRect.y<0;if(isOut){$(".navbar").addClass("navbar-compact")}else{$(".navbar").removeClass("navbar-compact")}},{threshold:[1],delay:100});observer.observe(document.getElementById("scroll-down"))}});