$(document).ready(function(){var frmDestroyPlanilla=$("#frm-destroy-planilla");frmDestroyPlanilla.on("submit",function(event){event.preventDefault();if($(this).valid()){confirmar("","¿Está seguro que desea desechar la planilla?","question",function(){waitingDialog.show();$.ajax({type:"DELETE",url:frmDestroyPlanilla.attr("action"),data:frmDestroyPlanilla.serialize(),success:function(response){if(response.success){mensaje_swal("success",response.message,function(){waitingDialog.show();window.location.href=response.redirect},false,null,"Continuar")}else{}},error:function(error){mensaje_swal("error",error)}})})}else{return false}});$("#btn-delete-planilla").on("click",function(evt){frmDestroyPlanilla.submit()})});